INSERT INTO wp_if_posts VALUES('3315', '3', '2012-06-22 10:04:35', '2012-06-22 18:04:35', '<h5>design features</h5>\r\nAn stylishly rounded seat is the center point to the Lara Dining Chair. Crafted from a \r\ndurable steel frame,it is upholstered in fine Italian leather. Its rectangular back \r\ncomplements the seat,and the overall effect is truly unique modernity.\r\n<em>(Available in a wide selection of leather color choices. Stocked\r\nin brown leather.)</em> ', 'Lara', '', 'publish', 'closed', 'closed', '', 'lara-2', '', '', '2012-06-29 16:04:44', '2012-06-30 00:04:44', '', '0', 'http://www.inspirationfurniture.ca/?post_type=products&#038;p=3315', '0', 'products', '', '0');
INSERT INTO wp_if_posts VALUES('43', '1', '2011-09-19 04:43:03', '2011-09-19 04:43:03', 'Inspiration Furniture - Vancouver, owned by brothers Peter and Steen Skaaning, opened in August 2002 and is located in the 40,000 square feet showroom, within the New Look Building in the historic landmark, Sigurdson\'s Mill, in False Creek. The building, which features free covered parking, 3 stories, 5 levels, natural light and greenery and a 100-year-old original timber construction, is home to a Boconcept store, INspiration Furniture, Natuzzi Italia Gallery and the latest addition, a Stressless Comfort Gallery - all under one roof.\r\n\r\nA leader in contemporary and modern furnishings, at all price points, INspiration is Vancouver\'s fastest growing furniture company. A 60,000 square feet warehouse and distribution center ensures that most items are in-stock for immediate delivery and installation by in-house professionals. As well, INspiration offers in-store design services and a large Special Order program.', 'Company', '', 'inherit', 'open', 'open', '', '5-revision-2', '', '', '2011-09-19 04:43:03', '2011-09-19 04:43:03', '', '5', 'http://staging.interfacemedia.ca/inspirationfurniture/5-revision-2/', '0', 'revision', '', '0');
