INSERT INTO wp_if_posts VALUES('2448', '2', '2011-11-07 18:46:30', '2011-11-07 18:46:30', '<img src=\"http://inspirationfurniture.ca/wp-content/uploads/showroom.jpg\" alt=\"\" title=\"Showroom\" width=\"765\" height=\"190\" class=\"alignnone size-full wp-image-2265\" /></a>\r\n\r\nInspiration Furniture - Vancouver, owned by brothers Peter and Steen Skaaning, opened in August 2002 and is located in the 40,000 square feet showroom, within the New Look Building in the historic landmark, Sigurdson\'s Mill, in False Creek. The building, which features free covered parking, 3 stories, 5 levels, natural light and greenery and a 100-year-old original timber construction, is home to a Boconcept store, INspiration Furniture, Natuzzi Italia Gallery and the latest addition, a Stressless Comfort Gallery - all under one roof.\r\n\r\nA leader in contemporary and modern furnishings, at all price points, INspiration is Vancouver\'s fastest growing furniture company. A 60,000 square feet warehouse and distribution center ensures that most items are in-stock for immediate delivery and installation by in-house professionals. As well, INspiration offers in-store design services and a large Special Order program.<img src=\"http://inspirationfurniture.digicoyote.net/wp-content/uploads/StaffPhoto2.jpg\" alt=\"\" title=\"Staff Photo\" width=\"764\" height=\"321\" class=\"alignnone size-full wp-image-2225\" /></a>', 'About Inspiration', '', 'inherit', 'open', 'open', '', '5-revision-36', '', '', '2011-11-07 18:46:30', '2011-11-07 18:46:30', '', '5', 'http://inspirationfurniture.ca/5-revision-36/', '0', 'revision', '', '0');
INSERT INTO wp_if_posts VALUES('1157', '3', '2011-10-25 03:57:07', '2011-10-25 03:57:07', '<h5>design features</h5>\r\nCreate the modern bedroom of your dreams with the Magnolia Bed. Simplicity \r\nis combined with a contemporary flair for a look that is all your own made \r\nof sustainable bamboo. Spacious and delightfully comfortable bed with solid \r\nwood headboard . <em>Available in king or queen.</em>\r\n\r\n<h5>dimensions</h5>\r\n224 x 183 x 75(h)cm\r\n\r\n&nbsp;\r\n&nbsp;\r\n', 'Magnolia', '', 'publish', 'closed', 'closed', '', 'magnolia-4', '', '', '2012-01-25 19:46:33', '2012-01-25 19:46:33', '', '0', 'http://inspirationfurniture.digicoyote.net/?post_type=products&#038;p=1157', '0', 'products', '', '0');
