INSERT INTO wp_if_posts VALUES('2881', '3', '2012-02-11 01:33:59', '2012-02-11 01:33:59', '<h5>design features</h5>\r\nSensuously shaped with timeless elements; that characterises the work of Dutch designer \r\nGerard Vollenbrock. In the Mundo armchair he mixes style, elegance and a compact \r\nlook with power and character. The little feet and the compact cup of the seat are \r\narchitectural style elements that somehow invoke nostalgic emotions but at the same time \r\nare completely of today. Contemporary yet completely familiar. That is a mark of \r\nworld-class design. \r\n\r\n', 'Mundo', '', 'publish', 'closed', 'closed', '', 'mundo', '', '', '2012-02-11 01:36:59', '2012-02-11 01:36:59', '', '0', 'http://inspirationfurniture.ca/?post_type=products&#038;p=2881', '0', 'products', '', '0');
