INSERT INTO wp_if_posts VALUES('3680', '3', '2012-11-13 18:06:28', '2012-11-14 02:06:28', '<h5>design features</h5>\r\nA sleek, sophisticated and stylish addition to your bedroom. Featuring the popular platform \r\nbed design, this high profile bed features a tufted bonded leather headboard, side rails \r\nand tufted foot board which combines to add a contemporary vibe to your space. \r\n<em>Available in white, mink, black and mocha.</em>\r\n\r\n<h5>dimensions</h5>\r\nQueen: 160cm(w) x 223.5cm(l) x 124.5cm(h)\r\nKing:  200.5cm(w) x 223.5(l) x 124.5cm(h)\r\n\r\n', 'Zen ', '', 'publish', 'closed', 'closed', '', 'zen-2', '', '', '2013-01-06 12:28:26', '2013-01-06 20:28:26', '', '0', 'http://www.inspirationfurniture.ca/?post_type=products&#038;p=3680', '0', 'products', '', '0');
INSERT INTO wp_if_posts VALUES('3654', '3', '2012-10-23 15:34:41', '2012-10-23 23:34:41', '<h5>design features</h5>\r\nVintage Leather collection defines office comtemporary, unsurpassed quality and classic \r\ndesign. Built of a hardwood frame and all down feather cushions, each piece is covered in \r\nfinest, top grain leathers, which are tanned and aniline-dyed. Each piece in this vintage \r\nEnglish club series is finished on the frame, with seven stages of staining, sealing, and \r\nbuffing providing authentic distressing.', 'Newark', '', 'publish', 'closed', 'closed', '', 'newark', '', '', '2012-10-23 15:34:41', '2012-10-23 23:34:41', '', '0', 'http://www.inspirationfurniture.ca/?post_type=products&#038;p=3654', '0', 'products', '', '0');
